        and a.PINYIN  = #{pinyin}


        and a.SEX = #{sex}


        and a.BIRTHDAY  = #{birthday}


        and a.ORG_ID = #{orgId}


        and a.JOB_ID  = #{jobId}


        and a.MOBILE_NO  = #{mobileNo}


        and a.EMAIL  = #{email}


        and a.USER_TYPE = #{userType}


        and a.LAST_LOGIN_TIME  = #{lastLoginTime}


        and a.LAST_LOGIN_IP  = #{lastLoginIp}


        and a.LOGIN_COUNT = #{loginCount}


        and a.QQ_NO  = #{qqNo}


        and a.MSN_NO  = #{msnNo}


        and a.HOME_ADDRESS  = #{homeAddress}


        and a.USER_AVATAR  = #{userAvatar}


        and a.VCN_USER_ID  = #{vcnUserId}


        and a.VCN_STR  = #{vcnStr}


        and a.PTZ_LEVEL = #{ptzLevel}


        and a.IS_USE_VALIDITY = #{isUseValidity}


        and a.VALIDITY_BEGIN_DATE  = #{validityBeginDate}


        and a.VALIDITY_END_DATE  = #{validityEndDate}


        and a.SUB_SYS_PRIVILEGE  = #{subSysPrivilege}


        and a.IS_DEL = #{isDel}


        and a.ORDER_VALUE = #{orderValue}


        and a.ADD_ID  = #{addId}


        and a.ADD_TIME  = #{addTime}


        and a.MODIFY_ID  = #{modifyId}


        and a.MODIFY_TIME  = #{modifyTime}


        and a.CHANGE_FLAG = #{changeFlag}


        and a.OFFICE_PHONE  = #{officePhone}


        and a.OFFICE_ADDRESS  = #{officeAddress}


        and a.PROJECT_FLAG = #{projectFlag}


        and a.IS_SOCIAL_ACCOUNT = #{isSocialAccount}


        and a.VALIDITY_TYPE = #{validityType}


        and a.ID_CARD  = #{idCard}


        and a.POLICE_NO  = #{policeNo}


